import CoreDrawer from '@/components/common/Drawer';
import CoreToolbar from '@/components/common/Toolbar';
import CoreFooter from '@/components/common/Footer';
import CoreView from '@/components/common/View';
import BottomNav from '@/components/common/BottomNav';
export default {
  name: 'App',
  components: {
    CoreDrawer: CoreDrawer,
    CoreToolbar: CoreToolbar,
    CoreFooter: CoreFooter,
    CoreView: CoreView,
    BottomNav: BottomNav
  },
  data: function data() {
    return {
      refreshing: false,
      registration: null,
      snackBtnText: '',
      snackWithBtnText: '',
      snackWithButtons: false,
      timeout: 6000 //

    };
  },
  created: function created() {
    var _this = this;

    // Listen for swUpdated event and display refresh snackbar as required.
    document.addEventListener('swUpdated', this.showRefreshUI, {
      once: true
    }); // Refresh all open app tabs when a new service worker is installed.

    navigator.serviceWorker.addEventListener('controllerchange', function () {
      if (_this.refreshing) return;
      _this.refreshing = true;
      window.location.reload();
    });
  },
  methods: {
    showRefreshUI: function showRefreshUI(e) {
      this.registration = e.detail;
      this.snackBtnText = 'Refresh';
      this.snackWithBtnText = 'New version available!';
      this.snackWithButtons = true;
    },
    refreshApp: function refreshApp() {
      this.snackWithButtons = false;

      if (!this.registration || !this.registration.waiting) {
        return;
      }

      this.registration.waiting.postMessage('skipWaiting');
    }
  }
};
